mapper.delete:
DELETE FROM FP_FreepReviewLike 
WHERE mb_no = #{mb_no}  
AND review_no = #{review_no}

mapper.select:
SELECT COUNT(*) FROM FP_FreepReviewLike
WHERE review_no = #{review_no}
ORDER BY review_like_no DESC

SELECT COUNT(*)
FROM FP_FreepReviewLike
WHERE review_no = #{review_no}
AND mb_no = #{mb_no}
ORDER BY review_like_no DESC
